import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStethoscope } from '@fortawesome/fontawesome-free-solid';
import React, { Component, Fragment } from 'react';
import { Container, Row, Col } from 'react-bootstrap';

class Department extends Component {
    constructor(){
        super();
        this.state = {
            departmentCom: "departmentComponent",
            departmentIco: "departmentIcon",
            departmentTi: "departmentTitle",
            departmentSub: "departmentSubTitle"
        }
    }

    onHover = () => {
        if(window.scrollY > 100){
            this.setState({departmentSubTitle: "departmentSubTitleHover", departmentIco: "departmentIconHover", departmentTi: "departmentTitleHover", departmentSub:"departmentSubTitleHover"})
        }
        else if(window.scrollY < 100){
            this.setState({departmentSubTitle: "departmentComponent", departmentIco: "departmentIcon", departmentTi: "departmentTitle", departmentSub: "departmentSubTitle"})
        }
    }

    componentDidMount(){
        window.addEventListener('mouseover', this.onHover)
    }

    render() {
        return (
            <Fragment>
                <Container fluid={true} className="text-center">
                    <h1 className="freeConsultHeading">DEPARTMENT</h1>
                    <Row>
                        <Col sm={12} md={6} lg={3} className={this.state.departmentCom}>
                            <FontAwesomeIcon icon={faStethoscope} className={this.state.departmentIco} />
